mapper.insert:
INSERT INTO WAREHOUSE(SECTION_NO, RECEIVE_CODE, RECEIVE_ITEM_NO, ITEM_CODE, COUNT, CREATE_IP, CREATE_ID)
VALUES
    (#{WAREHOUSE_SECTION_NO},#{RECEIVE_CODE},#{RECEIVE_ITEM_NO},#{ITEM_CODE},#{RECEIVE_COUNT},#{CREATE_IP},#{CREATE_ID});

mapper.update:
UPDATE RECEIVE
SET RECEIVE_DATE = CURRENT_TIMESTAMP,
    MODIFY_DATE = CURRENT_TIMESTAMP,
    MODIFY_IP = #{receiveModifyDto.modifyIp},
    MODIFY_ID = #{receiveModifyDto.modifyId}
WHERE RECEIVE_CODE = #{receiveCode}

mapper.select:
SELECT RECEIVE_CODE, RECEIVE_DATE, CREATE_DATE, CREATE_IP, CREATE_ID, MODIFY_DATE, MODIFY_IP, MODIFY_ID
FROM RECEIVE
WHERE RECEIVE_CODE = #{receiveCode};
